import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import axios from 'axios';
import './App.css';

// NavBar Component
const NavBar = () => (
  <nav className="nav">
    <img src="/logo.png" alt="Netflix Logo" className="nav__logo" />
    <input type="text" placeholder="Search" className="nav__search" />
  </nav>
);

// MovieCard Component
const MovieCard = ({ movie }) => (
  <div className="movie-card">
    <img src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
    <h3>{movie.title}</h3>
  </div>
);

// MovieRow Component
const MovieRow = ({ title, fetchUrl }) => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const request = await axios.get(fetchUrl);
      console.log(request.data.results); // Check the data
      setMovies(request.data.results);
      return request;
    };
    fetchData();
  }, [fetchUrl]);

  return (
    <div className="movie-row">
      <h2>{title}</h2>
      <div className="movie-row__posters">
        {movies.map(movie => (
          <MovieCard key={movie.id} movie={movie} />
        ))}
      </div>
    </div>
  );
};

// HomePage Component
const HomePage = () => {
  const API_KEY = 'YOUR_API_KEY'; // Replace with your TMDB API key
  const requests = {
    fetchTopRated: `/movie/top_rated?api_key=${API_KEY}&language=en-US`,
    fetchActionMovies: `/discover/movie?api_key=${API_KEY}&with_genres=28`,
    fetchComedyMovies: `/discover/movie?api_key=${API_KEY}&with_genres=35`,
  };

  return (
    <div className="homepage">
      <NavBar />
      <MovieRow title="Top Rated" fetchUrl={requests.fetchTopRated} />
      <MovieRow title="Action" fetchUrl={requests.fetchActionMovies} />
      <MovieRow title="Comedy" fetchUrl={requests.fetchComedyMovies} />
    </div>
  );
};

// Main App Component
function App() {
  return (
    <Router>
      <div className="app">
        <Switch>
          <Route path="/" exact component={HomePage} />
          {/* Define the route for movie details here if needed */}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
