// src/pages/HomePage.jsx
import React from 'react';
import MovieRow from '../components/MovieRow';
import requests from '../requests'; // Crie um arquivo requests.js para gerenciar as URLs da API
import NavBar from '../components/NavBar';
import './HomePage.css'; // Estilize conforme necessário

const HomePage = () => {
    return (
        <div className="homepage">
            <NavBar />
            <MovieRow title="Top Rated" fetchUrl={requests.fetchTopRated} />
            <MovieRow title="Action" fetchUrl={requests.fetchActionMovies} />
            <MovieRow title="Comedy" fetchUrl={requests.fetchComedyMovies} />
            {/* Adicione mais categorias conforme necessário */}
        </div>
    );
};

export default HomePage;
